import axios from 'axios';
import React, { useEffect, useState } from 'react';
import './Card.css'; // Import any specific styles for the Card component

function Cards({ movieId }) {
    const [movieDetails, setMovieDetails] = useState(null);

    useEffect(() => {
        if (movieId) {
            axios
                .get(`https://api.themoviedb.org/3/movie/${movieId}?api_key=YOUR_API_KEY`) // Replace with your actual API key
                .then((res) => {
                    setMovieDetails(res.data); // Set the movie details
                })
                .catch((err) => {
                    alert(err.message);
                });
        }
    }, [movieId]);

    if (!movieDetails) {
        return <p>Loading...</p>;
    }

    return (
        <div className="card-container">
            <img
                src={`https://image.tmdb.org/t/p/w500${movieDetails.poster_path}`}
                alt={movieDetails.original_title}
                className="card-image"
            />
            <div className="card-details">
                <h2 className="card-title">{movieDetails.original_title}</h2>
                <p className="card-runtime">{movieDetails.release_date}</p>
                <p className="card-description">{movieDetails.overview.slice(0, 120)}...</p>
            </div>
        </div>
    );
}

export default Cards;
