import React, { useEffect, useState } from "react";
import Skeleton, { SkeletonTheme } from "react-loading-skeleton";
import "./card.css";
import { Link } from "react-router-dom";
import axios from "axios";

const Cards = ({ movie }) => {
    const [isLoading, setIsLoading] = useState(true);
    const [isAddedToFavorites, setIsAddedToFavorites] = useState(false); // Track if movie is already in favorites

    useEffect(() => {
        setTimeout(() => {
            setIsLoading(false);
        }, 1500);
    }, []);

    const addFavourite = async () => {
        try {
            const token = localStorage.getItem("token");
            if (!token) {
                alert("You must be logged in to add to favorites");
                return;
            }
    
            console.log("Token:", token);
            console.log("Movie ID:", movie.id);
    
            const response = await axios.post(
                "http://localhost:3001/api/auth/addfavourites", // API endpoint
                { movieId: movie.id }, // Send movieId in the request body
                {
                    headers: {
                        Authorization: `Bearer ${token}`, // Attach token to Authorization header
                    },
                }
            );
    
            if (response.status === 201) {
                setIsAddedToFavorites(true); // Update state to reflect the added favorite
                alert("Movie added to favorites!");
            }
        } catch (error) {
            console.error("Error adding to favorites:", error.message);
            alert("Failed to add to favorites");
        }
    };
    
    return (
        <>
            {isLoading ? (
                <div className="cards">
                    <SkeletonTheme color="#202020" highlightColor="#444">
                        <Skeleton height={300} duration={2} />
                    </SkeletonTheme>
                </div>
            ) : (
                <div className="cards">
                <Link to={`/movie/${movie.id}`} style={{ textDecoration: "none", color: "white" }}>
                   
                        <img
                            className="cards__img"
                            src={`https://image.tmdb.org/t/p/original${movie ? movie.poster_path : ""}`}
                            alt={movie ? movie.original_title : "movie poster"}
                        />
                        <div className="cards__overlay">
                            <div className="card__title">{movie ? movie.original_title : ""}</div>
                            <div className="card__runtime">
                                {movie ? movie.release_date : ""}
                                <span className="card__rating">
                                    {movie ? movie.vote_average : ""}
                                    <i className="fas fa-star" />
                                </span>
                            </div>
                            <div className="card__description">
                                {movie ? movie.overview.slice(0, 118) + "..." : ""}
                            </div>
                           
                        </div>
                        </Link>
                         <button 
                                onClick={addFavourite}
                                disabled={isAddedToFavorites}
                                className={`${isAddedToFavorites ? "disabled-button" : ""} cards__overlay `}
                            >
                                {isAddedToFavorites ? "Added to Favorites" : "Add to Favorites"}
                            </button>
                    </div>
                
            )}
        </>
    );
};

export default Cards;
