import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Cards from '../../components/card/card';

function Search() {
    const { keyword } = useParams();
    const [searchData, setSearchData] = useState([]);

    useEffect(() => {
        console.log(keyword);
        
        fetch(`https://api.themoviedb.org/3/search/movie?api_key=6c61c32058c985e7f2122b55&query=${keyword}&include_adult=false&language=en-US&page=1`) 
            .then((res) => res.json()) // Add parentheses to call `json()`.
            .then((response) => setSearchData(response.results || [])) // Use `response.results` since the data contains `results`.
            .catch((err) => alert(err.message));
    }, [keyword]); // Include `keyword` as a dependency to re-run when it changes.

    return (
        <>
            {searchData.length > 0 ? (
                searchData.map((movie) => (
                    <Cards key={movie.id} movie={movie} /> // Ensure unique `key` for each element.
                ))
            ) : (
                <p>No movies found.</p>
            )}
        </>
    );
}

export default Search;
