import React, { useState, useEffect } from "react";
import "./header.css";
import { Link, useNavigate } from "react-router-dom";

const Header = () => {
    const navigate = useNavigate();
    const [inputValue, setInputValue] = useState("");
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    // Check user login status (mock implementation for now)
    useEffect(() => {
        const token = localStorage.getItem("token"); // Assuming a token is stored after login
        setIsLoggedIn(!!token); // Set to true if token exists
    }, []);

    const searchKeyword = () => {
        if (inputValue) {
            navigate(`search/${inputValue}`);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem("token"); // Remove token to log out
        setIsLoggedIn(false);
        navigate("/"); // Navigate to the home page after logout
    };

    return (
        <div className="header">
            <div className="headerLeft">
                <Link to="/">
                    <img
                        className="header__icon"
                        src="https://upload.wikimedia.org/wikipedia/commons/thumb/6/69/IMDB_Logo_2016.svg/2560px-IMDB_Logo_2016.svg.png"
                        alt="IMDB Logo"
                    />
                </Link>
                <Link to="/movies/popular" style={{ textDecoration: "none" }}>
                    <span>Popular</span>
                </Link>
                <Link to="/movies/top_rated" style={{ textDecoration: "none" }}>
                    <span>Top Rated</span>
                </Link>
                <Link to="/movies/upcoming" style={{ textDecoration: "none" }}>
                    <span>Upcoming</span>
                </Link>
                <input
                    value={inputValue}
                    onChange={(e) => setInputValue(e.target.value)}
                    placeholder="Search"
                />
                <button onClick={searchKeyword}>Search</button>
            </div>
            <div className="headerRight">
                {!isLoggedIn ? (
                    <>
                        <button onClick={() => navigate("/signup")} className="authButton">
                            Signup
                        </button>
                        <button onClick={() => navigate("/login")} className="authButton">
                            Login
                        </button>
                    </>
                ) : (
                    <button onClick={handleLogout} className="authButton">
                        Logout
                    </button>
                )}
            </div>
        </div>
    );
};

export default Header;
