import axios from 'axios';
import React, { useEffect, useState } from 'react';
import './Profile.css'; // Import the CSS for styling

function Profile() {
    const [favouritesMovies, setFavouritesMovies] = useState([]);
    const [isViewingFavorites, setIsViewingFavorites] = useState(false); // State to toggle favorite view
    const token = localStorage.getItem('token');

    useEffect(() => {
        if (token) {
            axios
                .get("http://localhost:3001/api/auth/getfavourites", {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                })
                .then((res) => {
                    setFavouritesMovies(res.data.favourites);
                })
                .catch((err) => {
                    alert(err.message);
                });
        }
    }, [token]);

    const username = "John Doe"; // Replace this with the username fetched from the backend or token

    const toggleFavoritesView = () => {
        setIsViewingFavorites(!isViewingFavorites); // Toggle the display of favorites
    };

    return (
        <div className="profile-container">
            <div className="profile-header">
                <h1>Welcome, {username}</h1>
            </div>

            <div className="profile-body">
                <button className="favorites-button" onClick={toggleFavoritesView}>
                    {isViewingFavorites ? 'Hide Favorites' : 'View Favorites'}
                </button>

                {isViewingFavorites && favouritesMovies.length > 0 && (
                    <div className="favorites-list">
                        {favouritesMovies.map((movie, index) => (
                            <div className="favorite-card" key={index}>
                                <img
                                    src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                                    alt={movie.original_title}
                                    className="movie-poster"
                                />
                                <div className="movie-details">
                                    <h2>{movie.original_title}</h2>
                                    <p>{movie.release_date}</p>
                                    <p>{movie.overview.slice(0, 120)}...</p>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
                {isViewingFavorites && favouritesMovies.length === 0 && (
                    <p>No favorites found.</p>
                )}
            </div>
        </div>
    );
}

export default Profile;
